+function ($) {
    "use strict";
    var DF = {
        type: 'success',
        message: 'TMAlert v1.0',
        timeout: 0
    }
    $.fn.TMAlert = function (op) {
        return $(this).each(function () {
            $.extend(DF, op);
            var isThis = $(this);
            var class_type = 'alert-success';
            if (DF.type == 'error' || DF.type == 2)
                class_type = "alert-danger";
            else if (DF.type == 'warning' || DF.type == 3)
                class_type = 'alert-warning';
            else if (DF.type == 'notice' || DF.type == 4)
                class_type = 'alert-info';

            isThis.html(set_alert(class_type, DF.message)).children('.alert').fadeIn();
            if (parseInt(DF.timeout) > 0)
                setTimeout(function () {
                    isThis.fadeOut('slow', function () {
                        $(this).remove()
                    });
                }, DF.timeout);
            console.log(class_type);
        });
    }
    function set_alert(class_type, message) {
        return '<div style="display:none" class="alert ' + class_type + ' alert-dismissible">' +
            '<button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">Ã—</span></button>' + message + '</div>'
    }
}(jQuery);
+function ($) {
    "use strict";
    var DF = {
        data: null,
        key: {title: 'Parent', icon: 'fa fa-connectdevelop', level: '0'},
        hidden: {id: null, sId: ',', level: '-1'},
        dHidden: {id: null, sId: ',', level: '-1'},
        //selected: {id: 'id', value: null, index: null},
        selected: 0,
        noParent: false,
        caret: ' <span class="caret"></span>',
        classCss: ['custom-drop', 'dropdown-menu-none'],
        fncSelected: function (event, target) {
        }
    };

    $.fn.SelectDropdownParent = function (op) {
        return $(this).each(function () {
            $.extend(DF, op);
            var isThis = $(this);
            //
            selected(isThis);
            //
            $(get_ul(isThis)).on('click', 'li a', function () {
                $(get_a(isThis)).html($(this).html() + DF.caret);
                //
                $(get_ul(isThis)).find('a').removeClass('selected');
                $(this).addClass('selected');
                //
                set_data(isThis, $(this).attr('data-value'));
            })
        });
    };
    function get_a(obj) {
        return $(obj).children('a');
    }

    function get_ul(obj) {
        return $(obj).children('ul');
    }

    function selected(obj) {
        create_data(obj);
        var items = get_ul(obj).find('a');
        var item = items[items.length - 1];
        get_a(obj).html($(item).html() + DF.caret);
        //
        if (typeof (DF.selected) == 'string') {
            for (var i = 0; i < items.length; i++) {
                //console.log($(items[i]).attr('data-value'));
                if ($(items[i]).attr('data-value') == DF.selected) {
                    item = items[i];
                    get_a(obj).html($(item).html() + DF.caret);
                    break;
                }

                //console.log($(items[i]).attr('data-value'));
            }

            // DF.selected = DF.selected > item.length ? item.length - 1 : DF.selected
            // get_a(obj).html($(item[DF.selected]).html() + DF.caret);
        }
        //
        else if (typeof (DF.selected) == 'number') {
            DF.selected = DF.selected > items.length ? items.length - 1 : DF.selected
            item = items[DF.selected];
            get_a(obj).html($(item).html() + DF.caret);
        }
        $(item).addClass('selected');
        set_data(obj, $(item).attr('data-value'));

    }

    function create_data(obj) {
        var hidden = '<input type="hidden" name="" value="">';
        for (var i = 0; i < (DF.data).length; i++) {
            $(obj).append('<input type="hidden" name="' + DF.data[i] + '" value="">');
        }
    }

    function set_data(obj, val) {
        var hidden = $(obj).find('input[type=hidden]');
        for (var i = 0; i < (hidden).length; i++) {
            $(hidden[i]).val(val);
            // if ($(hidden[i]).attr('name') == data[0])
            //     $(hidden[i]).val(data[1]);
        }
    }
}(jQuery);
